function.program_code:
if (args[0] == null)
	return null;
DateTime date = DateTime.Parse(args[0].ToString()); int month = date.Month;
if (month >= 1 && month <= 3) 
	return "1";
if (month >= 4 && month <= 6) 
	return "2"; 
if (month >= 7 && month <= 9) 
	return "3"; 
if (month >= 10 && month <= 12) 
	return "4"; 
return "0";

if (args[0] == null)
	return null;
DateTime date = DateTime.Parse(args[0].ToString()); int month = date.Month;
if (month >= 1 && month <= 3) 
	return "Q1";
if (month >= 4 && month <= 6) 
	return "Q2"; 
if (month >= 7 && month <= 9) 
	return "Q3"; 
if (month >= 10 && month <= 12) 
	return "Q4"; 
return "0";

if (args[0] == null)
	return null;
//create return value
string monthName = "";

//create int to hold month num
int monthNumber;

//create datetime to hold date
DateTime myDate;

//see if input is an integer or a date
bool intCheck = Int32.TryParse(args[0].ToString(), out monthNumber);
bool dateCheck = DateTime.TryParse(args[0].ToString(), out myDate);

//make appropriate conversion based on input type
if(intCheck && monthNumber > 0 && monthNumber < 13)
	monthName = System.Globalization.CultureInfo.CurrentCulture.DateTimeFormat.GetMonthName(monthNumber);
else if(dateCheck)
	monthName = System.Globalization.CultureInfo.CurrentCulture.DateTimeFormat.GetMonthName(myDate.Month);
else
	monthName = sessionInfo.PageInfo.LanguageDict.GetInfo("FormulaEditorMonthNameError").Html;

//return converted month name or failure message
return monthName;

if (args[0] == null)
	return null;
DateTime date = DateTime.Parse(args[0].ToString());
return System.Globalization.CultureInfo.InvariantCulture.Calendar.GetWeekOfYear(date, CalendarWeekRule.FirstDay, DayOfWeek.Monday);

if (args[0] == null)
	return null;
DateTime date = DateTime.Parse(args[0].ToString());
int day = (int)date.DayOfWeek + 1;
return sessionInfo.PageInfo.LanguageDict.GetInfo("CalendarDayOfWeek" + day).Html;

if (args[0] == null)
	return null;
DateTime date = DateTime.Parse(args[0].ToString());
return (int)System.Globalization.CultureInfo.InvariantCulture.Calendar.GetDayOfWeek(date) + 1;
